mapper.update:
update member set
member_level = (select member_level from member where
member_id=#{memberId})*-1 where member_id=#{memberId}

mapper.delete:
delete from

	my_trip where trip_no=#{boardNo}


	recommend_board where rec_no=#{boardNo}


	together_board where together_no=#{boardNo}


	tour where item_no=#{boardNo}

mapper.select:
select
trip_board_writer as reportedMember from trip_board where trip_board_no=#{boardNo}
rec_writer as reportedMember from recommend_board where rec_no=#{boardNo}
together_writer as reportedMember from together_board where together_no=#{boardNo}
board_class from report
