mapper.select:

select count(*) as cnt from tour join region using(region_no) where close_check != -1

	and member_id = #{memberId}


	and close_check!=1



		and region_no=#{regionNo}


		and region_country=#{regionCountry}



	and begin_date < #{tourDate} and #{tourDate} < end_date


	and (item_title like #{searchValue} or region_country like #{searchValue} or region_city like #{searchValue})

select * from( select ROWNUM as rnum, p.* from( select t.item_no as itemNo, m.member_name as memberName, t.item_title as itemTitle, t.region_no as regionNo, t.begin_date as beginDate, t.end_date as endDate, p.filename, t.close_check as closeCheck, NVl((select avg(s.review_rate) from tour_review s where (s.item_no = t.item_no)), 0) as score, NVL((select Count(*) from tour_reserve c where (c.item_no=t.item_no)),0) as reserveCnt from tour t join member m on(m.member_id=t.member_id) join photo p on(p.board_no=t.item_no and p.board_class=4) join region r on(r.region_no=t.region_no) where close_check != -1
and t.member_id=#{memberId}
and t.close_check != 1
and t.region_no=#{regionNo}
and r.region_country=#{regionCountry}
and t.begin_date <= #{tourDate} and #{tourDate} <= t.end_date
and (t.item_title like #{searchValue} or r.region_country like #{searchValue} or r.region_city like #{searchValue})
order by
item_no
score
reserveCnt
desc
asc
)p) where rnum between #{start} and #{end}

select * from
(select ROWNUM as rnum, r.* from
(select review_no as reviewNo, item_no as itemNo, member_id as memberId, member_nickname as memberNickname, review_rate as reviewRate, review_content as reviewContent, review_date as reviewDate
from tour_review join member using(member_id) where item_no=#{itemNo} order by review_no desc)r)
where rnum between #{start} and #{end}
