mapper.select:
select message_no as messageNo, message_sender as messageSender, message_receiver as messageReceiver, message_content as messageContent, send_date as sendDate, message_check as messageCheck, delete_level as deleteLevel from message
where delete_level in (0,2) and message_receiver = #{messageReceiver}
where delete_level in (0,1) and message_sender = #{messageSender}
order by message_no desc

mapper.update:
update message
set message_check = 1
where message_no = #{_parameter}

mapper.delete:
delete from message
where message_no in

	#{msg.messageNo}
