mapper.select:
select * from( select rownum as rnum, b.* from (
select trip_board_title as boardTitle, trip_board_date as boardDate, read_count as readCount from trip_board where trip_board_writer = #{memberId}
union all
select rec_title as boardTitle, rec_date as boardDate, read_count as readCount from RECOMMEND_BOARD where rec_writer = #{memberId}
union all
select together_title as boardTitle, together_date as boardDate, read_count as readCount from TOGETHER_BOARD where together_writer = #{memberId}
order by boardDate desc) b)blist where rnum between #{start} and #{end}

select * from( select rownum as rnum, mlist.* from( select m.* from (
select bookmark_no as bookmarkNo, trip_board_title as boardTitle, trip_board_writer as boardWriter from trip_board join bookmark on (trip_board_no = board_no) where board_class=1 and member_id = #{memberId}
union all
select bookmark_no as bookmarkNo, rec_title as boardTitle, rec_writer as boardWriter from recommend_board join bookmark on (rec_no = board_no) where board_class=2 and member_id = #{memberId}
) m order by bookmarkno desc)mlist order by rnum) where rnum between #{start} and #{end}

select count(*) from( select rownum, b.* from (
select trip_board_title as boardTitle, trip_board_date as boardDate, read_count as readCount from trip_board where trip_board_writer = #{memberId}
union all
select rec_title as boardTitle, rec_date as boardDate, read_count as readCount from RECOMMEND_BOARD where rec_writer = #{memberId}
union all
select together_title as boardTitle, together_date as boardDate, read_count as readCount from TOGETHER_BOARD where together_writer = #{memberId}
order by boardDate desc) b) blist

select count(*) from( select rownum, m.* from (
select trip_board_title as boardTitle, trip_board_writer as boardWriter from trip_board join bookmark on (trip_board_no = board_no) where board_class=1 and member_id = #{memberId}
union all
select rec_title as boardTitle, rec_writer as boardWriter from recommend_board join bookmark on (rec_no = board_no) where board_class=2 and member_id = #{memberId}
) m order by rownum desc)
